```scala
import scala.util.Random

object HistoricalFictionGenerator extends App {
  val protagonists = List("the brave knight", "the mysterious mercenary", "the skilled archer")
  val artifacts = List("the Ancient Relic", "the Sacred Sword", "the Enchanted Stone")
