% Load an image
input_image = imread('sample_image.jpg');

% Convert the image to grayscale
gray_image = rgb2gray(input_image);

% Measure 1: Use built-in function for edge detection
edges = edge(gray_image, 'Canny');

% ###### Measure 2: Use parallel processing for filtering ######
num_filters = 4;
processed_image = cell(num_filters, 1);

% Define some kernels for filtering
kernels = cell(num_filters, 1);
kernels{1} = fspecial('average', 7);
kernels{2} = fspecial('gaussian', [13 13], 4); 
