% Use 'parfor' loop for parallel processing
parfor i = 1:num_filters
    processed_image{i} = imfilter(gray_image, kernels{i});
end

% ###### Measure 3: Use vectorized operations instead of for loops ######
[rows, cols] = size(gray_image);
contrast_img = mat2gray(gray_image, [min(gray_image(:)), max(gray_image(:))]);

% Measure 4: Preallocate memory for arrays
result = cell(1, num_filters);
% Combine the results
for i = 1:num_filters
    result{i} = uint8(zeros(rows, cols));
end

% Measure 5: GPU acceleration in MATLAB
gray_image_gpu = gpuArray(gray_image);
edges_gpu = gpuArray(edges);

% Apply filters to the edge-detected image and accumulate results (for this example, we're using the GPU acceleration in edge detection)
