```
JavaScript:
```javascript
function sortTable(n) {
  let table, rows, switching, i, x, y, shouldSwitch, dir, switchcount = 0;
  table = document.getElementById("sortable-table");
  switching = true;
  dir = "asc";

  while (switching) {
    switching = false;
    rows = table.rows;

    /* Make a loop that will capitalize the first letter of every row */
    for (i = 1; i < rows.length - 1; i++) {
      shouldSwitch = false;
      rows[i].classList.add("switch-row");
      rows[i].setAttribute("data-row-original-index", i);

      for (x = i + 1; x < rows.length; x++) {
        rows[x].classList.add("switch-row");
        rows[x].setAttribute("data-row-original-index", x);

        if (rows[i].children[n].textContent > rows[x].children[n].textContent) {
          shouldSwitch = true;
          break;
        }
      }

      if (shouldSwitch) {
        rows[i].classList.remove("display-row");
        rows[x].classList.add("display-row");
        rows[i].setAttribute("data-row-original-index", rows[x].getAttribute("data-row-original-index"));
        rows[x].setAttribute("data-row-original-index", i);
        switching = true;
        switchcount++;
      }
    }

    if (switchcount === 0) {
      if (dir === "asc") {
        dir = "desc";
      } else {
        dir = "asc";
      }
      switching = true;
      switchcount++;
    }
  }
}

document.getElementById("sortable-table").addEventListener("DOMMouseScroll", function() {
  window.scrollTo({
    top: 0,
    behavior: "smooth"
  });
});
```
