from textblob import TextBlob

def sentiment_analysis(text):
    analysis = TextBlob(text)
    return analysis.sentiment.polarity

def respond_emotion(sentiment):
    if sentiment > 0.5:
        return "I'm glad you seem happy! How can I help you?"
    elif sentiment < -0.5:
        return "I'm sorry if you seem upset. How may I assist you?"
    else:
        return "How can I help you today?"

def name_extraction(text):
    word_list = text.split()
    for i, word in enumerate(word_list):
        if word.lower() in ["i'm", "i", "am", "my", "name", "is"]:
            try:
                return word_list[i + 1].strip(",")
            except IndexError:
                return None
    return None

def greet(text):
    response = "Hi there! "
    user_name = name_extraction(text)
    if user_name:
        response += f"Nice to meet you, {user_name}! "

    sentiment = sentiment_analysis(text)
    response += respond_emotion(sentiment)

    return response

def handle_query(query):
    query = query.lower()
    if "fact" in query or "tell me" in query:
        facts = [
            "Did you know that the first electronic computer weighed more than 27 tons?",
            "Did you know that around 50% of all clicks on mobile ads are accidental?",
            "Did you know that more than 570 new websites are created every minute?",
        ]
        return random.choice(facts)
    elif any(keyword in query for keyword in ["favorite", "color", "colour"]):
        return "My favorite color is blue."
    else:
        return "I'm sorry, I didn't understand your query. Please try again."

def store_info(name, info):
