            return db[name]
        except KeyError:
            return None

def chatbot_response(text):
    if text.startswith("Hi") or text.startswith("Hello") or text.startswith("Hey"):
        return greet(text)
    else:
        response = handle_query(text)
        return response

def main():
    print(textwrap.dedent("""
        Hi, I am your AI assistant. Feel free to ask any question or type 'quit' to exit.
    """))

