This program simulates a simple population of organisms with one trait, and applies selection pressures over several generations. The trait is coded as a randomly generated integer value for each individual organism, and the fitness is also randomly generated for each organism. The fittest individuals are allowed to breed and pass on their traits to the next generation, while the less fit individuals are likely to have no offspring or fewer offspring.

```java
import java.util.Random;

class Organism {
    private int trait;
    private double fitness;

    public Organism(int trait, double fitness) {
        this.trait = trait;
        this.fitness = fitness;
    }

    public int getTrait() {
        return trait;
    }

    public double getFitness() {
        return fitness;
    }
}

class Simulation {
    private int populationSize;
    private int generations;
    private Random random = new Random();
    private double mutationProbability;
    private double selectionPressure;
    private double offspringRate;

    private Organism[] population;

    public Simulation(int populationSize, int generations, double mutationProbability, double selectionPressure, double offspringRate) {
        this.populationSize = populationSize;
        this.generations = generations;
        this.mutationProbability = mutationProbability;
        this.selectionPressure = selectionPressure;
        this.offspringRate = offspringRate;

        this.population = new Organism[populationSize];

        // Initialize the population with random traits and fitness values
        for (int i = 0; i < populationSize; i++) {
            population[i] = new Organism(random.nextInt(100), random.nextDouble() * 100);
        }
    }

    public void run() {
        for (int i = 1; i <= generations; i++) {
            // Apply mutation to the population of organisms
            for (int j = 0; j < populationSize; j++) {
                if (random.nextDouble() < mutationProbability) {
                    population[j].trait = random.nextInt(100);
                }
            }

            // Apply selection to the population of organisms
            for (int j = 0; j < populationSize; j++) {
                for (int k = 0; k < populationSize; k++) {
                    if (population[j].getFitness() > population[k].getFitness()) {
                        // Select the fitter individual to produce offspring
                        population[k].setFitness(population[j].getFitness());
                    }
                }
            }

            // Apply offspring production to the population of organisms
            for (int j = 0; j < populationSize; j++) {
                population[j].setTrait(population[j].getTrait() + offspringRate);
            }

            // Evaluate the population of organisms
            double averageFitness = 0.0;
            for (int j = 0; j < populationSize; j++) {
                averageFitness += population[j].getFitness();
            }
            averageFitness /= populationSize;

            System.out.println("Generation " + i + ": Average fitness = " + averageFitness);
        }
    }
}

public class Main {
    public static void main(String[] args) {
        new Simulation(100, 100, 0.01, 1.0, 0.1).run();
    }
}
