```javascript
import { request } from 'request'; // npm install request

const postData = {
  key1: 'value1',
  key2: 'value2',
};

const fileToUpload = {
  path: 'path/to/file.txt',
  name: 'file.txt',
  type: 'text/plain',
  body: fs.createReadStream('path/to/file.txt'),
};

const options = {
  method: 'POST',
  headers: {
    'Content-Type': `multipart/form-data; boundary=${generateBoundary()}`,
    'Content-Disposition': `form-data; name="data";`,
  },
  body: formDataAppender(postData, fileToUpload),
  onUploadProgress: (progress) => {
    console.log(`Batch ${progress.batch} - Current: ${
        progress.loaded / progress.total
      } (${progress.totalBytes}/${progress.loadedBytes})`);
  },
};

function formDataAppender(postData: any, file: any) {
  const formData = new FormData();

  for (const key in postData) {
    formData.append(key, postData[key]);
  }

  formData.append('file', file.body, file.name, {
    contentType: file.type,
  });

  return formData;
}

request.post('https://example.com/upload', options, (error, response, body) => {
  if (error) throw new Error(`Error: ${error}`);
  console.log(`Status code: ${response.statusCode}`);
  console.log(`Body: ${JSON.stringify(body)}`);
});
```
