def CalculateField(inField: str, dictionary: dict, multifield: str, defaultValue: str):
    """
    inField: Input feature layer field name
    dictionary: Values to use for lookups
    multifield: Field to use for lookup
    defaultValue: Default value
    """
    if inField in dictionary.keys():
        value = dictionary[inField]
    else:
        for key, map_ref_value in dictionary.items():
