    recyclingInfo.put("aluminum cans", "Keep aluminum cans clean and dry, and place them in the recycling bin.");
    recyclingInfo.put("glass bottles", "Rinse out glass bottles and discard the lid before recycling.");
    recyclingInfo.put("paper", "Place clean and dry paper materials in the recycling bin.");
