    return info;
  }
}

public class Main {
  public static void main(String[] args) {
    RecyclingGuide guide = new RecyclingGuide();
    System.out.println("Recycling information for aluminum cans:\n" + guide.getRecyclingInfo("Aluminum Cans"));
  }
}
